</span></span><span style="display:flex;"><span>{  <span style="color:#00688b;font-weight:bold">int</span> Id;       <span style="color:#228b22">//活动ID
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>   <span style="color:#00688b;font-weight:bold">int</span> s_Time;   <span style="color:#228b22">//活动开始时间
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>   <span style="color:#00688b;font-weight:bold">int</span> f_Time;   <span style="color:#228b22">//活动结束时间
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>} ACND;
