</span></span><span style="display:flex;"><span>{
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">for</span>(<span style="color:#00688b;font-weight:bold">int</span> i=<span style="color:#b452cd">0</span>;i&lt;n;i++){
</span></span><span style="display:flex;"><span>        <span style="color:#00688b;font-weight:bold">int</span> tmp=i;
</span></span><span style="display:flex;"><span>        <span style="color:#8b008b;font-weight:bold">for</span>(<span style="color:#00688b;font-weight:bold">int</span> j=i+<span style="color:#b452cd">1</span>;j&lt;n;j++){
</span></span><span style="display:flex;"><span>            <span style="color:#8b008b;font-weight:bold">if</span>(arr[j].f_Time&lt;arr[tmp].f_Time){
</span></span><span style="display:flex;"><span>                tmp=j;
</span></span><span style="display:flex;"><span>            }
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>        ACND tmpSwap=arr[i];
</span></span><span style="display:flex;"><span>        arr[i]=arr[tmp];
</span></span><span style="display:flex;"><span>        arr[tmp]=tmpSwap;
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>}
</span></span><span style="display:flex;"><span><span style="color:#228b22">//进行贪心选择，得到最大相容的活动集合输出
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span><span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">Select</span>(<span style="color:#00688b;font-weight:bold">int</span> n,ACND arr[])
</span></span><span style="display:flex;"><span>{
</span></span><span style="display:flex;"><span>    <span style="color:#00688b;font-weight:bold">int</span> nTime=<span style="color:#b452cd">0</span>;
</span></span><span style="display:flex;"><span>    ACND*p=arr;
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">while</span>(p-arr&lt;n){
</span></span><span style="display:flex;"><span>        <span style="color:#8b008b;font-weight:bold">if</span>(p-&gt;s_Time&gt;=nTime){
</span></span><span style="display:flex;"><span>            cout&lt;&lt;p-&gt;Id&lt;&lt;<span style="color:#cd5555">&#34;:&#34;</span>&lt;&lt;p-&gt;s_Time&lt;&lt;<span style="color:#cd5555">&#34;-&#34;</span>&lt;&lt;p-&gt;f_Time&lt;&lt;endl;
</span></span><span style="display:flex;"><span>            nTime=p-&gt;f_Time;
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>        p++;
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>}
</span></span><span style="display:flex;"><span><span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">main</span>()
</span></span><span style="display:flex;"><span>{  ACND arr[Maxn];
</span></span><span style="display:flex;"><span>   <span style="color:#00688b;font-weight:bold">int</span> an,i;
