</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>   <span style="color:#8b008b;font-weight:bold">for</span>(i=<span style="color:#b452cd">0</span>;i&lt;an;i++)
</span></span><span style="display:flex;"><span>     cin&gt;&gt;arr[i].Id&gt;&gt;arr[i].s_Time&gt;&gt;arr[i].f_Time;
</span></span><span style="display:flex;"><span>   <span style="color:#228b22">//对活动按贪心准则进行排序
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>   Sort(an,arr);
</span></span><span style="display:flex;"><span>   <span style="color:#228b22">//进行贪心选择，获得最优解并输出
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>   Select(an,arr);
