</span></span><span style="display:flex;"><span>    mystack(){
</span></span><span style="display:flex;"><span>        maxsize=<span style="color:#b452cd">1</span>;
</span></span><span style="display:flex;"><span>        data=<span style="color:#8b008b;font-weight:bold">new</span> <span style="color:#00688b;font-weight:bold">char</span>[maxsize];
</span></span><span style="display:flex;"><span>        top=-<span style="color:#b452cd">1</span>;
</span></span><span style="display:flex;"><span>    }
