</span></span><span style="display:flex;"><span>            <span style="color:#8b008b;font-weight:bold">return</span> data[top];
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>        <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#cd5555">&#39;\0&#39;</span>;
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">pop</span>(){
</span></span><span style="display:flex;"><span>        <span style="color:#8b008b;font-weight:bold">if</span>(top&gt;=<span style="color:#b452cd">0</span>){
</span></span><span style="display:flex;"><span>            top--;
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">expand</span>(){
</span></span><span style="display:flex;"><span>        <span style="color:#00688b;font-weight:bold">char</span>*tmp=<span style="color:#8b008b;font-weight:bold">new</span> <span style="color:#00688b;font-weight:bold">char</span>[maxsize*<span style="color:#b452cd">2</span>];
</span></span><span style="display:flex;"><span>        strncpy(tmp,data,maxsize);
</span></span><span style="display:flex;"><span>        maxsize*=<span style="color:#b452cd">2</span>;
</span></span><span style="display:flex;"><span>        <span style="color:#8b008b;font-weight:bold">delete</span>[] data;
</span></span><span style="display:flex;"><span>        data=tmp;
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">push</span>(<span style="color:#00688b;font-weight:bold">char</span> x){
</span></span><span style="display:flex;"><span>        <span style="color:#8b008b;font-weight:bold">if</span>(top+<span style="color:#b452cd">1</span>==maxsize){
</span></span><span style="display:flex;"><span>            expand();
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>        data[++top]=x;
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>    ~mystack(){
</span></span><span style="display:flex;"><span>        <span style="color:#8b008b;font-weight:bold">delete</span>[] data;
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>};
