</span></span><span style="display:flex;"><span><span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">main</span>()
</span></span><span style="display:flex;"><span>{
</span></span><span style="display:flex;"><span>    string tmp;
</span></span><span style="display:flex;"><span>    getline(cin,tmp);
</span></span><span style="display:flex;"><span>    mystack*stk=<span style="color:#8b008b;font-weight:bold">new</span> mystack();
</span></span><span style="display:flex;"><span>    <span style="color:#00688b;font-weight:bold">bool</span> erred=<span style="color:#658b00">false</span>;
