</span></span><span style="display:flex;"><span>            <span style="color:#8b008b;font-weight:bold">case</span> -<span style="color:#b452cd">1</span>:
</span></span><span style="display:flex;"><span>                <span style="color:#228b22">//计算
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>                <span style="color:#00688b;font-weight:bold">double</span> tmp1,tmp2,tmp;
</span></span><span style="display:flex;"><span>                tmp2=OPND-&gt;getTop();
</span></span><span style="display:flex;"><span>                OPND-&gt;pop();
</span></span><span style="display:flex;"><span>                tmp1=OPND-&gt;getTop();
</span></span><span style="display:flex;"><span>                OPND-&gt;pop();
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">switch</span>(OPTR-&gt;getTop()){
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#39;+&#39;</span>:
</span></span><span style="display:flex;"><span>                    tmp=tmp1+tmp2;
</span></span><span style="display:flex;"><span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#39;-&#39;</span>:
</span></span><span style="display:flex;"><span>                    tmp=tmp1-tmp2;
</span></span><span style="display:flex;"><span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#39;*&#39;</span>:
</span></span><span style="display:flex;"><span>                    tmp=tmp1*tmp2;
</span></span><span style="display:flex;"><span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#39;/&#39;</span>:
</span></span><span style="display:flex;"><span>                    tmp=tmp1/tmp2;
</span></span><span style="display:flex;"><span>                    <span style="color:#228b22">//可以进行除数为0的判断
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#39;^&#39;</span>:
</span></span><span style="display:flex;"><span>                    tmp=pow(tmp1,tmp2);
</span></span><span style="display:flex;"><span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                }
</span></span><span style="display:flex;"><span>                OPND-&gt;push(tmp);
</span></span><span style="display:flex;"><span>                OPTR-&gt;pop();
</span></span><span style="display:flex;"><span>                i--;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">continue</span>;
</span></span><span style="display:flex;"><span>            <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">0</span>:
</span></span><span style="display:flex;"><span>                OPTR-&gt;pop();
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">continue</span>;
</span></span><span style="display:flex;"><span>            }
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>    res=OPND-&gt;getTop();
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">delete</span> OPTR;
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">delete</span> OPND;
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">return</span> res;
</span></span><span style="display:flex;"><span>}
</span></span><span style="display:flex;"><span><span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">main</span>()
</span></span><span style="display:flex;"><span>{
</span></span><span style="display:flex;"><span>    string str;
</span></span><span style="display:flex;"><span>    getline(cin,str);
</span></span><span style="display:flex;"><span>    str=<span style="color:#cd5555">&#34;#&#34;</span>+str;
</span></span><span style="display:flex;"><span>    str.erase(str.size()-<span style="color:#b452cd">1</span>);
</span></span><span style="display:flex;"><span>    str+=<span style="color:#cd5555">&#34;#&#34;</span>;
</span></span><span style="display:flex;"><span>    <span style="color:#00688b;font-weight:bold">double</span> res=cal(str);
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">if</span>(abs(<span style="color:#00688b;font-weight:bold">double</span>(<span style="color:#00688b;font-weight:bold">int</span>(res))-res)&lt;<span style="color:#b452cd">0.000001</span>){
</span></span><span style="display:flex;"><span>        cout&lt;&lt;res&lt;&lt;endl;
</span></span><span style="display:flex;"><span>    }<span style="color:#8b008b;font-weight:bold">else</span>{
</span></span><span style="display:flex;"><span>        cout&lt;&lt;setiosflags(ios::fixed)&lt;&lt;setprecision(<span style="color:#b452cd">5</span>)&lt;&lt;<span style="color:#00688b;font-weight:bold">int</span>(<span style="color:#b452cd">1e5</span>*res)/<span style="color:#b452cd">1e5</span>&lt;&lt;endl;
</span></span><span style="display:flex;"><span>    }
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">0</span>;
</span></span><span style="display:flex;"><span>}
