<div class="highlight"><pre tabindex="0" style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#1e889b">#include</span> <span style="color:#1e889b">&lt;iostream&gt;</span><span style="color:#1e889b">
</span><span style="color:#1e889b"></span><span style="color:#8b008b;font-weight:bold">using</span> <span style="color:#8b008b;font-weight:bold">namespace</span> std;
<span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">TMyDate</span>
{
<span style="color:#8b008b;font-weight:bold">private</span>:
    <span style="color:#00688b;font-weight:bold">int</span> yy,mm,dd;
    <span style="color:#00688b;font-weight:bold">bool</span> <span style="color:#008b45">isrn</span>(<span style="color:#00688b;font-weight:bold">int</span> year)<span style="color:#228b22">//判断闰年
</span><span style="color:#228b22"></span>    {
        <span style="color:#8b008b;font-weight:bold">if</span>((year%<span style="color:#b452cd">4</span>==<span style="color:#b452cd">0</span>&amp;&amp;year%<span style="color:#b452cd">100</span>!=<span style="color:#b452cd">0</span>)||(year%<span style="color:#b452cd">400</span>==<span style="color:#b452cd">0</span>))
        {
            <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#658b00">true</span>;
        }
        <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#658b00">false</span>;
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getDaysofmonth</span>(<span style="color:#00688b;font-weight:bold">int</span> y,<span style="color:#00688b;font-weight:bold">int</span> m)<span style="color:#228b22">//某年某月有多少天
</span><span style="color:#228b22"></span>    {
        <span style="color:#8b008b;font-weight:bold">switch</span>(m)
        {
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">1</span>:
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">3</span>:
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">5</span>:
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">7</span>:
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">8</span>:
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">10</span>:
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">12</span>:
            <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">31</span>;
        <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#b452cd">2</span>:
            <span style="color:#8b008b;font-weight:bold">if</span>(isrn(y)){
                <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">29</span>;
            }
            <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">28</span>;
        <span style="color:#8b008b;font-weight:bold">default</span>:
            <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">30</span>;
        }
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getTotaldays</span>(<span style="color:#00688b;font-weight:bold">int</span> y,<span style="color:#00688b;font-weight:bold">int</span> m,<span style="color:#00688b;font-weight:bold">int</span> d){<span style="color:#228b22">//某年月日距元年元月元日天数
</span><span style="color:#228b22"></span>        <span style="color:#00688b;font-weight:bold">int</span> sum=<span style="color:#b452cd">0</span>;
        sum+=d;
        m--;
        <span style="color:#8b008b;font-weight:bold">while</span>(m--){
            sum+=getDaysofmonth(y,m);
        }
        y--;
        <span style="color:#8b008b;font-weight:bold">while</span>(y--){
            sum+=isrn(y)?<span style="color:#b452cd">366</span>:<span style="color:#b452cd">365</span>;
        }
        <span style="color:#8b008b;font-weight:bold">return</span> sum;
    }
<span style="color:#8b008b;font-weight:bold">public</span>:
    TMyDate()
    {
        yy=<span style="color:#b452cd">1900</span>;
        mm=<span style="color:#b452cd">1</span>;
        dd=<span style="color:#b452cd">1</span>;
    }
    TMyDate(<span style="color:#00688b;font-weight:bold">int</span> y,<span style="color:#00688b;font-weight:bold">int</span> m,<span style="color:#00688b;font-weight:bold">int</span> d)
    {
        yy=y;
        mm=m;
        dd=d;
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getYear</span>(){
        <span style="color:#8b008b;font-weight:bold">return</span> yy;
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getMonth</span>(){
        <span style="color:#8b008b;font-weight:bold">return</span> mm;
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getDay</span>(){
        <span style="color:#8b008b;font-weight:bold">return</span> dd;
    }
    TMyDate <span style="color:#8b008b;font-weight:bold">operator</span>+(<span style="color:#00688b;font-weight:bold">int</span> d){<span style="color:#228b22">//日期加天数
</span><span style="color:#228b22"></span>        <span style="color:#00688b;font-weight:bold">int</span> tmp_d=dd;
        <span style="color:#00688b;font-weight:bold">int</span> tmp_m=mm;
        <span style="color:#00688b;font-weight:bold">int</span> tmp_y=yy;
        tmp_d+=d;
        <span style="color:#8b008b;font-weight:bold">while</span>(<span style="color:#658b00">true</span>){
            <span style="color:#8b008b;font-weight:bold">if</span>(tmp_d&gt;getDaysofmonth(tmp_y,tmp_m)){
                tmp_m++;
                tmp_d-=getDaysofmonth(tmp_y,tmp_m);
                <span style="color:#8b008b;font-weight:bold">continue</span>;
            }
            <span style="color:#8b008b;font-weight:bold">if</span>(tmp_m&gt;<span style="color:#b452cd">12</span>){
                tmp_y++;
                tmp_m-=<span style="color:#b452cd">12</span>;
                <span style="color:#8b008b;font-weight:bold">continue</span>;
            }
            <span style="color:#8b008b;font-weight:bold">break</span>;
        }
        <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#008b45">TMyDate</span>(tmp_y,tmp_m,tmp_d);
    }
    TMyDate <span style="color:#8b008b;font-weight:bold">operator</span>-(<span style="color:#00688b;font-weight:bold">int</span> d){<span style="color:#228b22">//日期减天数
</span><span style="color:#228b22"></span>        <span style="color:#00688b;font-weight:bold">int</span> tmp_d=dd;
        <span style="color:#00688b;font-weight:bold">int</span> tmp_m=mm;
        <span style="color:#00688b;font-weight:bold">int</span> tmp_y=yy;
        tmp_d-=d;
        <span style="color:#8b008b;font-weight:bold">while</span>(<span style="color:#658b00">true</span>){
            <span style="color:#8b008b;font-weight:bold">if</span>(tmp_d&lt;=<span style="color:#b452cd">0</span>){
                tmp_m--;
                tmp_d+=getDaysofmonth(tmp_y,tmp_m);
                <span style="color:#8b008b;font-weight:bold">continue</span>;
            }
            <span style="color:#8b008b;font-weight:bold">if</span>(tmp_m&lt;=<span style="color:#b452cd">0</span>){
                tmp_y--;
                tmp_m+=<span style="color:#b452cd">12</span>;
                <span style="color:#8b008b;font-weight:bold">continue</span>;
            }
            <span style="color:#8b008b;font-weight:bold">break</span>;
        }
        <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#008b45">TMyDate</span>(tmp_y,tmp_m,tmp_d);
    }
    <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#8b008b;font-weight:bold">operator</span>-(TMyDate&amp;d){<span style="color:#228b22">//日期与日期相距天数
</span><span style="color:#228b22"></span>        <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#008b45">getTotaldays</span>(yy,mm,dd)-getTotaldays(d.getYear(),d.getMonth(),d.getDay());
    }
    <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">showInfo</span>(){
        cout&lt;&lt;yy&lt;&lt;<span style="color:#cd5555">&#39;-&#39;</span>&lt;&lt;mm&lt;&lt;<span style="color:#cd5555">&#39;-&#39;</span>&lt;&lt;dd&lt;&lt;endl;
    }
};
<span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">main</span>()
{
    TMyDate tmp1(<span style="color:#b452cd">2018</span>,<span style="color:#b452cd">9</span>,<span style="color:#b452cd">16</span>);
