    TMyDate tmp3;
    tmp1.showInfo();
    tmp2.showInfo();
    tmp3=tmp1-<span style="color:#b452cd">25</span>;
    tmp3.showInfo();
    tmp3=tmp1+<span style="color:#b452cd">25</span>;
    tmp3.showInfo();
    cout&lt;&lt;tmp1-tmp2&lt;&lt;endl;
    tmp1.showInfo();
    tmp2.showInfo();
    <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">0</span>;
}
